local player=game.Players.LocalPlayer local character=player.Character local platformHeight=10 local function createPlatform()local platform=Instance.new("Part")platform.Size=Vector3.new(10,1,10)local humanoid=character:FindFirstChildOfClass("Humanoid")if humanoid then platform.Position=humanoid.RootPart.Position-Vector3.new(0,platformHeight,0)end platform.Transparency=1 platform.CanCollide=false platform.Parent=workspace game:GetService("RunService").Heartbeat:Connect(function()if humanoid then platform.Position=humanoid.RootPart.Position-Vector3.new(0,platformHeight,0)end end)end local function removePlatform()local platform=workspace:FindFirstChild("InvisiblePlatform")if platform then platform:Destroy()end end if player and character then character.Humanoid.Died:Connect(removePlatform)character.Humanoid.StateChanged:Connect(function(oldState,newState)if newState==Enum.HumanoidStateType.Jumping then createPlatform()elseif newState==Enum.HumanoidStateType.Landed then removePlatform()end end)end
